from fpdf import FPDFclass PDF(FPDF):    def __init__(self):        super().__init__()        # Charger les polices régulière, gras et italique        self.add_font("DejaVu", "", "DejaVu/DejaVuSansCondensed.ttf", uni=True)  # Régulière        self.add_font("DejaVu", "B", "DejaVu/DejaVuSansCondensed-Bold.ttf", uni=True)  # Gras        self.add_font("DejaVu", "I", "DejaVu/DejaVuSansCondensed-Oblique.ttf", uni=True)  # Italique        self.set_font("DejaVu", size=12)    def header(self, title="CURRICULUM VITÆ"):        # Ajouter un titre stylisé        self.set_font("DejaVu", "B", 16)        self.set_text_color(0, 0, 0)  # Noir        self.cell(0, 10, title, align="C", ln=1)        self.ln(10)    def footer(self):        # Ajouter un pied de page avec le numéro de page (seulement pour le CV)        if self.title == "CURRICULUM VITÆ":  # Vérifier si c'est le CV            self.set_y(-15)            self.set_font("DejaVu", "I", 8)            self.set_text_color(0, 0, 0)  # Noir            self.cell(0, 10, f"Page {self.page_no()}", align="C")    # Ajout de la méthode add_section à l'intérieur de la classe PDF    def add_section(self, title, content, font_size=9, is_list=False):        self.set_font("DejaVu", "B", font_size)        self.multi_cell(0, 5, title)  # réduire l'interligne du titre        self.ln(2)  # espace tout petit entre titre et contenu        self.set_font("DejaVu", "", font_size)        if is_list:            for item in content:                self.multi_cell(0, 4.5, f"- {item}")  # réduire l'interligne de la liste        else:            self.multi_cell(0, 4.5, content)  # réduire l'interligne du paragraphe# Données personnellesnom = "Régis Ferenc Mawugnon ATTOLOU"adresse = "Cotonou, Bénin"telephone = "+229 0168988452 / 0190919096"  # Numéro fictifemail = "regisattolou21@outlook.fr"  # Email fictifprofession = "Développeur Web Full Stack & Annotateur"  # Profession fictif# Contenu du CVprofil_professionnel = (    "Développeur Web Full Stack et Annotateur natif francophone avec un niveau intermédiaire en anglais."    "Rigoureux, organisé et doté d'une grande capacité d'adaptation, je suis motivé à contribuer efficacement"    "à des projets à distance nécessitant une excellente maîtrise du français et des outils numériques. "    "Toujours désireux d'apprendre et de progresser, je m'engage à fournir un travail de qualité dans un environnement multiculturel.")competences_cles = [    "Langues : Français (langue maternelle), Anglais (niveau intermédiaire).",    "Développement web : PHP, JavaScript, Python (bases), Java, MySQL, Angular, Laravel, Vue JS, React JS, Spring Boot.",    "Outils numériques : Microsoft Office, Google Workspace, Trello, Zoom.",    "Compétences interpersonnelles : Communication claire, écoute active, travail en équipe.",    "Autres compétences : Gestion du temps, résolution de problèmes, sens du service client."]atouts_supplementaires = [    "Adaptabilité rapide à de nouveaux outils et environnements de travail.",    "Capacité à travailler efficacement en équipe et à distance.",    "Esprit d'initiative et autonomie dans la gestion des tâches.",    "Engagement envers l'apprentissage continu et la montée en compétences."]motivation_poste = (    "Enthousiaste à l'idée de contribuer à des projets innovants dans un cadre de travail à distance, "    "je mets au service de votre entreprise mes compétences linguistiques, techniques et relationnelles."    "Mon objectif est d'évoluer au sein d’une structure dynamique où je pourrai enrichir mon expérience tout en apportant une réelle valeur ajoutée.")# Contenu de la Lettre de Motivationlettre_motivation = f"""Régis Ferenc Mawugnon ATTOLOUAdresse : Cotonou, BéninTéléphone: +229 0168988452 / 0190919096Email : regisattolou21@outlook.frCotonou, le 28 avril 2025Objet : Candidature pour un poste à distance nécessitant la maîtrise du françaisMadame, Monsieur,Titulaire d'une solide expérience dans le développement web et l'annotation de données, je souhaite vous proposer ma candidature pour un poste à distance nécessitant une excellente maîtrise du français. Francophone natif, je possède également un niveau intermédiaire en anglais, me permettant d’évoluer dans un environnement multiculturel.Rigoureux, organisé et doté d’une forte capacité d’adaptation, je suis à l’aise avec les outils numériques et la communication à distance. Ma volonté constante d’apprendre et de m’améliorer me permet d'intégrer rapidement de nouvelles méthodes de travail et de relever de nouveaux défis.Convaincu que la qualité du travail repose sur une communication fluide, la rigueur et l'esprit d'équipe, je m’engage à contribuer activement au succès de vos projets en mettant mes compétences techniques et relationnelles à votre service.Je serais honoré d’échanger avec vous pour discuter plus en détail de ma motivation et de mes compétences.Dans l’attente de votre retour, je vous prie d’agréer, Madame, Monsieur, l’expression de mes salutations distinguées.Régis Ferenc Mawugnon ATTOLOU"""# Fonction pour générer ledef generate_cv():    pdf = PDF()    pdf.title = "CURRICULUM VITÆ"    pdf.add_page()    # Informations de contact    pdf.set_font("DejaVu", "B", 10)  # police un peu plus petite    pdf.set_text_color(0, 0, 0)    pdf.multi_cell(0, 5, nom)  # interligne réduit    pdf.set_font("DejaVu", "", 9)    pdf.multi_cell(0, 5, f"Adresse : {adresse}")    pdf.multi_cell(0, 5, f"Téléphone : {telephone}")    pdf.multi_cell(0, 5, f"Email : {email}")    pdf.multi_cell(0, 5, f"Profession : {profession}")    pdf.ln(5)    # Sections du CV    pdf.add_section("PROFIL PROFESSIONNEL", profil_professionnel, font_size=9)    pdf.add_section("COMPETENCES CLÉS", competences_cles, font_size=9, is_list=True)    pdf.add_section("ATOUTS SUPPLÉMENTAIRES", atouts_supplementaires, font_size=9, is_list=True)    pdf.add_section("MOTIVATION POUR LE POSTE", motivation_poste, font_size=9)    pdf.output("cv.pdf")    print("Le CV a été généré avec succès sous le nom 'cv.pdf'.")# Fonction pour générer la lettre de motivationdef generate_motivation_letter():    pdf = PDF()    pdf.title = "LETTRE DE MOTIVATION"  # Définir le titre pour la lettre de motivation    pdf.add_page()    # Ajouter le contenu de la lettre de motivation    pdf.set_font("DejaVu", "", 12)    pdf.set_text_color(0, 0, 0)  # Noir    pdf.multi_cell(0, 10, lettre_motivation)    # Sauvegarder le fichier PDF    pdf.output("lettre_motivation.pdf")    print("La lettre de motivation a été générée avec succès sous le nom 'lettre_motivation.pdf'.")# Choix de l'utilisateurprint("Que souhaitez-vous générer ?")print("1. CV uniquement")print("2. Lettre de motivation uniquement")print("3. CV et lettre de motivation")choix = input("Entrez votre choix (1, 2 ou 3) : ")if choix == "1":    generate_cv()elif choix == "2":    generate_motivation_letter()elif choix == "3":    generate_cv()    generate_motivation_letter()else:    print("Choix invalide. Veuillez réessayer.")